INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('1', '1', 'Unfortunately, now many companies do not want to hire employees without experience.', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('2', '1', 'I study at a cool technical university and I have a lot of projects.', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('3', '1', 'After looking at the vacancies on HH, I realized that no one needs people without experience.', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('4', '1', 'I have made a good resume, but I get a few rejections every day.', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('5', '1', 'The only companies willing to hire people without experience are BIG-TECH, but there are a lot of people who want to go there, and even writing entrance exams and a motivation letter is good, everything depends on randomness.', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('6', '1', 'Help me, what do I need to do to find my first job?', 'Svytoq', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('7', '2', 'Długo szukaliśmy przepisu na prawdziwą shawarmę. Nie tylko pita z mięsem z kurczaka, ale soczysta shawarma-obfita, z dużą ilością mięsa i warzyw, ze wspaniałym sosem. Jednym słowem-przepis na domową shawarmę! :)', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('8', '2', 'Produkty (na 2 porcje): Chleb pita cienki - 2 liście, Filet z kurczaka ( duży) - 1 szt. Ser twardy - 200-250 g, Kapusta biała-150 g, Marchewka po koreańsku - 150 g, Ogórki kiszone - 2 małe, Pomidor świeży - 1 szt.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('9', '2', 'Majonez - 5-6 łyżek. łyżki, Śmietana - 2 łyżki. łyżki, Świeże zioła (pietruszka, koperek) - 0,5 małej pęczka, Czosnek - 2-4 ząbki (do smaku), Olej roślinny do smażenia - 2 łyżki. łyżki,Papryka mielona - 2 łyżeczki', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('10', '2', 'Pieprz cayenne - 1 szczypta (do smaku), Czosnek suszony granulowany - 1 łyżeczka, Przyprawa do kurczaka - 2 łyżeczki, Pieprz czarny mielony - do smaku, Sól-do smaku', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('11', '2', 'Najpierw zajmijmy się kurczakiem. Pokrój filet z kurczaka na plastry. Z jednego fileta otrzymasz około 4-5 kawałków.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('12', '2', 'Dobrze posolić kurczaka. Dopraw suszonym czosnkiem, mieloną papryką, przyprawą do kurczaka, pieprzem cayenne i czarnym pieprzem.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('13', '2', 'Dopraw olejem roślinnym i wymieszaj. Pozostaw filet z kurczaka do marynowania - od 15 minut do 1 godziny.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('14', '2', 'Zacznijmy robić SOS. Wymieszaj śmietanę i majonez.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('15', '2', 'Drobno posiekaj warzywa i wyślij do sosu.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('16', '2', 'Dodaj ząbki czosnku, obrane i wyciśnięte przez prasę.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('17', '2', 'Wymieszaj i pozostaw sos do zaparzenia.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('18', '2', 'Teraz zajmijmy się krojeniem warzyw. Posiekaj kapustę dość drobno.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('19', '2', 'Ogórki kiszone (można wziąć słone lub świeże) pokroić w plastry (cienkie talerze).', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('20', '2', 'Zetrzyj ser na drobnej tarce.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('21', '2', 'Rozgrzej patelnię grillową. Wlać i równomiernie rozprowadzić jedną łyżkę oleju roślinnego.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('22', '2', 'Połóż marynowanego kurczaka na patelni.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('23', '2', 'Smażymy kawałki fileta z kurczaka na złoty kolor, około 7-8 minut z każdej strony.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('24', '2', 'Podczas smażenia kurczaka zajmiemy się krojeniem pomidorów. Pomidory kroimy na końcu, ponieważ szybko wypuszczają sok.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('25', '2', 'Połóż gotowe kawałki kurczaka na folii i zawiń je losowo.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('26', '2', 'Odstawimy na 5 minut, aby smażony filet był bardziej soczysty.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('27', '2', 'Następnie pokrój kurczaka na mniejsze kawałki.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('28', '2', 'Rozwiń arkusz chleba pita. Połowę sosu smarujemy powierzchnią chleba pita wielkości przyszłej shawarmy, cofając się od krawędzi o szerokość nieco większą niż rozmiar shawarmy, aby następnie przykryć nadzienie tą wolną krawędzią.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('29', '2', 'Podziel wszystkie składniki na pół i ułóż warstwami chleb pita nasmarowany sosem. Pierwsza warstwa to zawsze mięso z kurczaka.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('30', '2', 'Drugi to kapusta.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('31', '2', 'Trzeci to ogórki.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('32', '2', 'Czwarty to pomidory.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('33', '2', 'Piąty to koreańska marchewka.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('34', '2', 'Aby uzyskać soczystość, możesz posmarować marchewki łyżką majonezu.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('35', '2', 'Szósta warstwa to ser. Nie żałuj tego, będzie smaczniejszy.)', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('36', '2', 'Teraz zacznijmy owijać shawarmę. Przykryj nadzienie mniejszą wolną krawędzią chleba pita.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('37', '2', 'Następnie podkręcamy dolną i górną krawędź.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('38', '2', 'I zawiń nadzienie w chleb pita w formie bułki. Tworzymy drugą shawarmę, powtarzając te same kroki.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('39', '2', 'Rozgrzej patelnię grillową (najpierw umyj ją lub dokładnie wytrzyj ręcznikiem papierowym po usmażeniu kurczaka. Rozłóż shawarmę na patelni.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
INSERT INTO MESSAGE (id, topic_id, text, author, created) VALUES ('40', '2', 'Smażyć shawarmę z obu stron. Gdy tylko wszystko w środku dobrze się rozgrzeje, a chleb pita stanie się rumiany i chrupiący, połóż gotową domową shawarmę na talerzu i podawaj.', 'MiłośnikKebabów776', '2024-04-10T00:28:30.643555900');
